import{_ as e}from"./chunks/interface.Cmf4eQuK.js";import{_ as o,c as n,o as i,ag as a}from"./chunks/framework.DbztrH9R.js";const u=JSON.parse('{"title":"Importing Contents - Audio Content Manager (ACM)","description":"","frontmatter":{"title":"Importing Contents - Audio Content Manager (ACM)"},"headers":[],"relativePath":"acm/importing-contents.md","filePath":"acm/importing-contents.md","lastUpdated":1746615007000}'),s={name:"acm/importing-contents.md"};function r(l,t,c,p,g,m){return i(),n("div",null,t[0]||(t[0]=[a('<h1 id="importing-contents" tabindex="-1">Importing Contents <a class="header-anchor" href="#importing-contents" aria-label="Permalink to &quot;Importing Contents&quot;">​</a></h1><p>Here’s a quick note about importing content:</p><p>According to the <a href="./../program-spec/introduction.html#contents">Program Specification</a>, a single message can be available in multiple languages. This means that in the ACM, you need to upload a separate file for each language.</p><p>For example, if a message is specified in both English and French, it must be recorded in both languages. As a result, you will need to import two separate files — one for English and one for French — into the ACM.</p><p>To import content into the ACM, follow these steps:</p><ol><li><a href="./installation.html#open-acm">Open the ACM</a></li><li>Click the menu icon (a laptop image) in the top-left corner of the ACM.</li><li>In the window that appears, select the <strong>Deployment</strong> and <strong>Language</strong> options.</li><li>Click <strong>Next</strong> to proceed.</li><li>Click <strong>Choose Audio File(s)</strong> and select the audio files you want to import.</li><li>Click <strong>Next</strong> to continue.</li><li>Match each audio file (in the <strong>Audio Files</strong> column) with its corresponding message (in the <strong>Title</strong> column).</li><li>Click <strong>Next</strong> to proceed.</li><li>Review your selections. If needed, click <strong>Prev</strong> to make changes.</li><li>Click <strong>Finish</strong> to complete the import process.</li></ol><div class="tip custom-block github-alert"><p class="custom-block-title">TIP</p><p>Once imported, the content will appear in the ACM interface, as shown below:</p></div><p><img src="'+e+'" alt="ACM Interface" loading="lazy"> The ACM interface displaying imported audio files.</p>',8)]))}const f=o(s,[["render",r]]);export{u as __pageData,f as default};
